import React, { useState, useEffect } from 'react';
import { getData, searchData, filterData, getStatisticsData } from './apiService';  // Add necessary imports

const DataTable = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stateFilter, setStateFilter] = useState('');
  const [firstNameSearch, setFirstNameSearch] = useState('');
  const [statistics, setStatistics] = useState(null);

  // Fetch all data
  const fetchData = async () => {
    try {
      const result = await getData();
      setData(result);
    } catch (error) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  // Fetch search data
  const searchDataByFirstName = async (firstName) => {
    try {
      const result = await searchData(firstName);
      setData(result);
    } catch (error) {
      setError('Failed to search data');
    }
  };

  // Fetch filtered data by state
  const filterDataByState = async (state) => {
    try {
      const result = await filterData(state);
      setData(result);
    } catch (error) {
      setError('Failed to filter data');
    }
  };

  // Fetch statistics
  const fetchStatistics = async () => {
    try {
      const result = await getStatisticsData();
      setStatistics(result);
    } catch (error) {
      setError('Failed to load statistics');
    }
  };

  // Effect for loading data initially
  useEffect(() => {
    fetchData();
  }, []);  // Only run once when the component mounts

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Data Table</h2>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by First Name"
        value={firstNameSearch}
        onChange={(e) => setFirstNameSearch(e.target.value)}
      />
      <button onClick={() => searchDataByFirstName(firstNameSearch)}>Search</button>

      {/* Filter by state */}
      <input
        type="text"
        placeholder="Filter by State"
        value={stateFilter}
        onChange={(e) => setStateFilter(e.target.value)}
      />
      <button onClick={() => filterDataByState(stateFilter)}>Filter</button>

      {/* Show Statistics */}
      <button onClick={fetchStatistics}>Show Statistics</button>

      {/* Display statistics */}
      {statistics && (
        <div>
          <h3>Statistics</h3>
          <ul>
            {statistics.map((stat) => (
              <li key={stat.state}>
                <strong>{stat.state}</strong> - Avg Age: {stat.averageAge}, Most Common First Name: {stat.mostCommonFirstName}, Most Common Last Name: {stat.mostCommonLastName}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Data Table */}
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Age</th>
            <th>Street</th>
            <th>City</th>
            <th>State</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>Credit Card</th>
          </tr>
        </thead>
        <tbody>

        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
